contracts/bridgev2/StakingVote.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

// Vote and run Router swapIn

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";


contract StakingVote is Ownable {
    using SafeMath for uint;

    address public stakingCoin;
    uint public lockTime = 15 days;

    uint public allStaking;

    struct staking {
        uint amount;
        uint lock;
        uint withdrawTime;
    }

    mapping(address => staking) public userStaking;

    struct vote {
        uint totalVoteAmount;
        uint haveVoteAmount;
        mapping(address => uint) userVote;
    }

    mapping(bytes32 => vote) public txVotes;


    event LogStake(address from, address coin, uint amount);
    event LogUnStake(address from, address coin, uint amount);
    event LogWithDraw(address from, address coin, uint amount);
    event LogVote(address from, address coin, uint amount, bytes32 tx);

    constructor(address coin){
        stakingCoin = coin;
    }

    modifier onlyStaking(address sender){
        require(userStaking[sender].amount > 0, "only staking");
        _;
    }

    modifier canWithdraw(address sender){
        staking memory v = userStaking[sender];
        require(v.withdrawTime != 0 && v.withdrawTime < block.timestamp, "Withdrawal time is up");
        _;
    }

    function _stake(address sender, uint amount) private {
        staking storage stak = userStaking[sender];
        stak.amount = stak.amount.add(amount);
        allStaking = allStaking.add(amount);
        emit LogStake(sender, stakingCoin, amount);
    }

    function stake(uint amount) external payable {
        if (stakingCoin == address(0)) {
            require(msg.value == amount, "amount is error");
        } else {
            IERC20(stakingCoin).transferFrom(msg.sender, address(this), amount);
        }
        _stake(msg.sender, amount);
    }


    function withdraw() external canWithdraw(msg.sender) {
        uint amount = _withdraw();
        if (stakingCoin == address(0)) {
            payable(msg.sender).transfer(amount);
        } else {
            IERC20(stakingCoin).transfer(msg.sender, amount);
        }
        _withdraw();
    }

    function _withdraw() private returns (uint){
        staking storage stak = userStaking[msg.sender];
        uint amount = stak.lock;
        stak.lock = 0;
        stak.withdrawTime = block.timestamp;
        emit LogUnStake(msg.sender, stakingCoin, amount);
        return amount;
    }

    function unStaking(uint amount) external {
        _unStaking(amount);
    }

    function _unStaking(uint amount) private {
        staking storage stak = userStaking[msg.sender];
        require(amount <= stak.amount, "not have amount");
        stak.amount = stak.amount.sub(amount);
        stak.lock = stak.lock.add(amount);
        stak.withdrawTime = block.timestamp.add(lockTime);
    }

    function checkVoteAmount(uint have, uint all) public pure returns (bool){
        return have > (all.mul(2).div(3));
    }

    function voteTx(bytes32 hash, address voter) onlyStaking(voter) external returns (bool){
        vote storage v = txVotes[hash];
        if (v.totalVoteAmount ==0){
            v.totalVoteAmount = allStaking;
        }
        require(v.haveVoteAmount < v.totalVoteAmount, "vote is compile");
        uint amount = userStaking[voter].amount;
        v.haveVoteAmount = v.haveVoteAmount.add(amount);
        emit LogVote(voter, stakingCoin, amount, hash);
        return checkVoteAmount(v.haveVoteAmount, v.totalVoteAmount);
    }

}

contracts/bridgev2/Router.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interface/ITxVerify.sol";
import "./interface/IMapERC20.sol";
import "./TokenRegister.sol";
import "./interface/IVote.sol";

contract Router is ReentrancyGuard, Ownable {

    event LogSwapIn(bytes32 hash, address indexed token, address indexed from, address indexed to, uint amount, uint fromChainID, uint toChainID);
    event LogSwapOut(bytes32 hash, address indexed token, address indexed from, address indexed to, uint amount, uint fromChainID, uint toChainID);
    event LogSwapInFail(bytes32 hash, string message, address indexed from, address indexed to, uint amount, uint fromChainID, uint toChainID);

    uint public nonce;
    uint public chainID;

    mapping(bytes32 => bool) hashHandle;

    TokenRegister tokenRegister;
    ITxVerify verify;
    IVote vote;

    constructor(TokenRegister _tokenRegister){
        tokenRegister = _tokenRegister;
        uint _chainId;
        assembly {_chainId := chainid()}
        chainID = _chainId;
    }

    modifier checkToken(uint fromChain, address source, uint toChain){
        require(address(0) != tokenRegister.getToToken(fromChain, source, toChain), "target token is empty");
        _;
    }


    modifier checkOrderHash(bytes32 hash){
        require(!hashHandle[hash], "order hash is have");
        _;
    }

    function setVerify(address _verify) public onlyOwner {
        verify = ITxVerify(_verify);
    }

    function setVote(address _vote) public onlyOwner {
        vote = IVote(_vote);
    }

    function setOrderHash(bytes32 hash) public {
        hashHandle[hash] = true;
    }

    function getNextTransactionID(address from, address token, address to, uint amount, uint toChainID) public returns (bytes32){
        nonce ++;
        return keccak256(abi.encodePacked(nonce, from, token, to, amount, chainID, toChainID));
    }


    function _swapIn(bytes32 hash, address token, address from, address to, uint amount, uint fromChainID) internal {
        _withdraw(token, amount, to);
        emit LogSwapIn(hash, token, from, to, amount, fromChainID, chainID);
    }

    function _swapBridge(bytes32 hash, address token, address from, address to, uint amount, uint toChainID) internal {
        emit LogSwapOut(hash, token, from, to, amount, chainID, toChainID);
    }


    function _withdraw(address _token, uint amount, address to) public {
        IMapERC20 token = IMapERC20(_token);
        if (token.isMap()) {
            token.mint(to, amount);
        } else {
            token.transfer(to, amount);
        }
    }

    function swapIn(bytes32 hash, address token, address from, address to, uint amount, uint fromChainID, uint toChainID,
        address router, bytes memory txProve)
    external checkOrderHash(hash) nonReentrant() {
        if (txProve.length > 10) {
            (bool succ,string memory message) = verify.txVerify(router, token, fromChainID, toChainID, txProve);
            require(succ, "very fail");
            emit LogSwapInFail(hash,message,from,to,amount,fromChainID,toChainID);
        } else {
            require(vote.voteTx(hash,msg.sender), "vote fail");
            emit LogSwapInFail(hash,"vote fail",from,to,amount,fromChainID,toChainID);
        }
        if (toChainID == chainID) {
            _swapIn(hash, token, from, to, amount, fromChainID);
        } else {
            _swapBridge(hash, token, from, to, amount, toChainID);
        }
        setOrderHash(hash);
    }


    function _swapOut(address from, address token, address to, uint amount, uint toChainID) internal {
        address toToken = tokenRegister.getToToken(chainID, token, toChainID);
        require(toToken != address(0), "Other token is not Regisger");
        bytes32 hash = getNextTransactionID(from, token, to, amount, toChainID);
        _lock(token, amount);
        emit LogSwapOut(hash, toToken, from, to, amount, chainID, toChainID);
    }

    function _lock(address _token, uint amount) public {
        IMapERC20 token = IMapERC20(_token);
        if (token.isMap()) {
            token.burn(msg.sender, amount);
        } else {
            token.transferFrom(msg.sender, address(this), amount);
        }
    }

    // msg.sender deposit @amount @token to cross-chain transfer to @to at chain @toChainID
    function swapOut(address token, address to, uint amount, uint toChainID) external nonReentrant {
        _swapOut(msg.sender, token, to, amount, toChainID);
    }
}

